var uniqueLetterString = function (s) {
  let arr = new Array(),
    res = 0;

  let help = (s) => {
    let map = new Map(),
      count = 0;
    for (let i = 0; i < s.length; i++) {
      if (!map.has(s[i])) {
        map.set(s[i], 1);
      } else {
        map.set(s[i], map.get(s[i]) + 1);
      }
    }
    for (let i = 0; i < s.length; i++) {
      if (map.get(s[i]) == 1) count++;
    }
    return count;
  };

  for (let i = 0; i < s.length; i++) {
    for (let j = i + 1; j <= s.length; j++) {
      arr.push(s.substring(i, j));
    }
  }

  for (let i = 0; i < arr.length; i++) {
    res += help(arr[i]);
  }

  return res;
};











console.log(
  uniqueLetterString(
    ""
  )
);
